Scripts.OnClick:
if ( IsModifiedClick("CHATLINK") and self.link ) then
  HandleModifiedItemClick(self.link);
end

if ( IsModifiedClick("CHATLINK") and self.link ) then
  HandleModifiedItemClick(self.link);
end

local currentTab = self:GetParent():GetID();
if ( AGB.currentTab ~= currentTab ) then
  PlaySound("GuildBankOpenBag");
end
ArmoryIconGuildBankTab_OnClick(self, button, currentTab);

if ( button == "RightButton" and self.canDelete ) then
  ArmoryStaticPopup_Show("ARMORY_DELETE_GUILDBANK", ArmoryGuildBankFrame.selectedGuild);
end

Scripts.OnEnter:
self.canDelete = (ArmoryGuildBankFrame.selectedRealm ~= AGB.realm or ArmoryGuildBankFrame.selectedGuild ~= AGB.guild);
if ( self.canDelete ) then
  GameTooltip:SetOwner(self, "ANCHOR_BOTTOMRIGHT");
  GameTooltip:SetText(ARMORY_DELETE_UNIT_HINT, GREEN_FONT_COLOR.r, GREEN_FONT_COLOR.g, GREEN_FONT_COLOR.b);
end

Scripts.OnEditFocusLost:
self:HighlightText(0, 0);
if ( self:GetText() == "" ) then
  self:SetText(SEARCH);
end

Scripts.OnEditFocusGained:
self:HighlightText();
if ( self:GetText() == SEARCH ) then
  self:SetText("");
end

Scripts.OnEnter:
GameTooltip_SetDefaultAnchor(GameTooltip, self);
Armory:AddEnhancedTip(self, SEARCH, 1.0, 1.0, 1.0, string.format(ARMORY_INVENTORY_SEARCH_TEXT_TOOLTIP,
  ITEM_QUALITY0_DESC, ITEM_QUALITY1_DESC, ITEM_QUALITY2_DESC, ITEM_QUALITY3_DESC, ITEM_QUALITY4_DESC, ITEM_QUALITY5_DESC, ITEM_QUALITY6_DESC, ITEM_QUALITY7_DESC), 1);

Scripts.OnMouseDown:
if ( button == "LeftButton" ) then
  self:StartMoving();
  self.isMoving = true;
end

Scripts.OnMouseUp:
if ( self.isMoving ) then
  self:StopMovingOrSizing();
  self.isMoving = false;
end

Scripts.OnMouseDown:
if ( button == "LeftButton" ) then
  self:StartMoving();
  self.isMoving = true;
end

Scripts.OnMouseUp:
if ( self.isMoving ) then
  self:StopMovingOrSizing();
  self.isMoving = false;
end
